const Menu = ({ items }) => {
 const fillingUp = "--------------------------------------------------"
  return (
    <div className="section-center">
      {items.map((menuItem) => {
        const { id, title, img, desc, price } = menuItem;
        return (
          <article key={id} className="menu-item">
            <img src={img} alt={title} className="photo" />
            <div className="item-info">
              <header>
                <h4>{title}</h4>
                <h4 className="price">â‚¬ {price}</h4>
                <h6>{desc.length}</h6>
              </header>
                {desc.length < 50 ? <p className="item-text"> {desc + (50-desc.length) } </p> : <p className="item-text">{desc}  </p> }
              

            </div>
          </article>
        );
      })}
    </div>
  );
};

export default Menu;
